<script type="text/javascript">function initialize() {    var <?php echo $map_id; ?>_Options = {        mapTypeId: google.maps.MapTypeId.<?php echo $instance['maptype']; ?>,        zoom: <?php echo $instance['zoomlevel']; ?>,        center: new google.maps.LatLng(<?php echo $gmap_iconizer_latitude; ?>,<?php echo $gmap_iconizer_longitude; ?>),        mapTypeControl: <?php echo $instance['maptypecontrol']; ?>,        mapTypeControlOptions: {            style: google.maps.MapTypeControlStyle.<?php echo $instance['maptypecontroldefault']; ?>,            position: google.maps.ControlPosition.<?php echo $instance['maptypecontrolposition']; ?>        },        panControl: <?php echo $instance['pancontrol']; ?>,        panControlOptions: {            position: google.maps.ControlPosition.<?php echo $instance['pancontrolposition']; ?>        },        zoomControl: <?php echo $instance['zoomcontrol']; ?>,        zoomControlOptions: {            style: google.maps.ZoomControlStyle.<?php echo $instance['zoomcontrolsize']; ?>,            position: google.maps.ControlPosition.<?php echo $instance['zoomcontrolposition']; ?>        },        scaleControl: false,        streetViewControl: <?php echo $instance['streetviewcontrol']; ?>,        streetViewControlOptions: {        position: google.maps.ControlPosition.<?php echo $instance['streetviewposition']; ?>}    }    var map = new google.maps.Map(document.getElementById('<?php echo $map_id; ?>'),    <?php echo $map_id; ?>_Options);    var styles = [ {        featureType: "all",        stylers: [        { hue: <?php if($instance['color']!='#ffffff')            { echo '"'.$instance['color'].'"';}            else {                echo '0';            } ?>        },        { saturation: <?php if($instance['saturation']!= '')            { echo $instance['saturation'];}            else            { echo '0';}        ?> }        ]    }    ];    map.setOptions({styles: styles});    var image = '<?php if($instance['icon']!='') {        echo $instance['icon'];    }    else {        echo $gmap_iconizer_upload_icon;    }    ?>';    var myLatLng = new google.maps.LatLng(<?php echo $gmap_iconizer_latitude; ?>,<?php echo $gmap_iconizer_longitude; ?>);    var <?php echo $map_id; ?>_div = new google.maps.Marker({        position: myLatLng,        map: map,        icon: image,        divid: ""    });}google.maps.event.addDomListener(window, 'load', initialize);</script><style>.gm-style img { max-width: none; }.gm-style label { width: auto; display: inline; }</style><div class="<?php echo $map_id; ?>_container" style="<?phpif( $instance['containerstyle']!='') {    echo $instance['containerstyle'];}else {    echo $gmap_iconizer_mapcontainerstyle;}?><?php if($gmap_iconizer_mapwidth != ""){ echo "width:";    echo $gmap_iconizer_mapwidth;echo ";";}else{ echo "width:450px;";} ?><?php if($gmap_iconizer_mapheight != ""){ echo "height:";    echo $gmap_iconizer_mapheight;echo ";";}else{ echo "height:200px;";} ?>">    <div id="<?php echo $map_id; ?>" style="<?php if($gmap_iconizer_mapwidth != ""){        echo "width:".$gmap_iconizer_mapwidth.";";    }else    { echo 'width:450px;';} ?>    <?php if($gmap_iconizer_mapheight != ""){ echo 'height:'.$gmap_iconizer_mapheight.';';    }else    { echo 'height:400px';} ?> <?php echo $gmap_iconizer_mapstyle;?>    ">    </div>    </div>